import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Box, Typography, TextField, Button } from '@mui/material';
import VoterTable from './VoterTable';
import { searchByNameAction } from '../redux/actions/action';
import { Icon } from "@iconify/react";

const VolunteerData = () => {
  const dispatch = useDispatch();
  const [searchName, setSearchName] = useState('');
  const [searched, setSearched] = useState(false); // Track if user has searched
  const [filteredVoters, setFilteredVoters] = useState([]);
  const voters = useSelector((state) => state.searchByNameReducer?.data);

  // Update filteredVoters whenever voters state changes
  useEffect(() => {
    if (voters) {
      setFilteredVoters(voters.filter(item => !item.casted));
    }
  }, [voters]);

  const handleSearch = () => {
    dispatch(searchByNameAction(searchName));
    setSearched(true); // Set searched to true after searching
  };

  const handleClear = () => {
    setSearchName('');
    setFilteredVoters([]);
    setSearched(false); // Reset searched status when clearing
  };

  return (
    <Box sx={{ padding: 3, display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <Box>
        <Box sx={{ display: 'flex', mb: 3, justifyContent: 'center' }}>
          <TextField
            size="small"
            placeholder="Search by entering a name"
            variant="outlined"
            value={searchName}
            onChange={(e) => setSearchName(e.target.value)}
            sx={{
              "& .MuiOutlinedInput-root": {
                "&.Mui-focused .MuiOutlinedInput-notchedOutline": {
                  borderColor: "#3e4241",
                },
                "&:hover .MuiOutlinedInput-notchedOutline": {
                  borderColor: "#3e4241",
                },
                "& .MuiOutlinedInput-notchedOutline": { borderColor: "#3e4241", }
              },
              mr: 2
            }}
          />

          <Button
            variant="contained"
            onClick={handleSearch}
            sx={{
              backgroundColor: '#EE8832',
              color: 'white',
              fontWeight: 'bold',
              '&:hover': {
                backgroundColor: '#EE8832',
                color: 'white',
              },
            }}
          >
            Search
          </Button>
        </Box>
        {searched && filteredVoters.length === 0 ? (
          <img src="https://t4.ftcdn.net/jpg/05/86/21/03/360_F_586210337_WOGOw0l7raEB8F61Muc4hWbvVcyQdk9Z.jpg" alt="No Data" style={{ width: '100%', maxWidth: '400px', margin: '0 auto' }} />
        ) : searched && filteredVoters.length > 0 ? (
          <VoterTable voters={filteredVoters} handleClear={handleClear} />
        ) : (
          <Typography variant="body1" sx={{ textAlign: 'center' }}>Enter input to search</Typography>
        )}
      </Box>
    </Box>
  );
};

export default VolunteerData;
