// LoginForm.js

import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { TextField, Button, Typography, Box, Grid } from '@mui/material';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const LoginForm = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ email: '', password: '' });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3000/auth/login', formData);
      const { user_type, token, username } = response.data;

      localStorage.setItem('userType', user_type);
      localStorage.setItem('token', token);
      localStorage.setItem('name', username);

      toast.success('Login successful!', {
        position: 'top-right',
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: 'colored',
      });

      // if (user_type === 'volunteer') {
      //   navigate('/VolunteerData');
      // } else if (user_type === 'lead') {
      //   navigate('/LeadData');
      // }
    } catch (error) {
      console.error('Login error:', error);
      toast.error('Login failed. Please check your email and password.', {
        position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "colored",
      });
    }
  };

  return (
    <>
      <Grid container justifyContent="center" alignItems="center" sx={{ height: '100vh' }}>
        <Grid item xs={12} sm={8} md={6} lg={3.5}>
          <Box sx={{ boxShadow: '5px 4px 8px 5px rgba(0, 0, 0, 0.2)', padding: '80px 30px', borderRadius: '10px' }}>
            <Box sx={{ textAlign: 'center', mb: '20px' }}>
              <Typography variant="h4" sx={{ fontWeight: '700' }}>
                Sign in
              </Typography>
            </Box>
            <form onSubmit={handleSubmit}>
              <Box mb={3}>
                <TextField
                  fullWidth
                  label="Email"
                  name="email"
                  type="email"
                  required
                  value={formData.email}
                  onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                />
              </Box>
              <Box mb={5}>
                <TextField
                  fullWidth
                  label="Password"
                  name="password"
                  type="password"
                  required
                  value={formData.password}
                  onChange={(e) => setFormData({ ...formData, password: e.target.value })}
                />
              </Box>
              <Box mt={2}>
                <Button
                  fullWidth
                  type="submit"
                  variant="contained"
                  sx={{ backgroundColor: 'black', fontSize: '17px', fontWeight: 'bold', letterSpacing: '2px', padding: '10px','&:hover': {
                    backgroundColor: 'black',
                    color: 'white'
                  } }}
                >
                  Login
                </Button>
              </Box>
            </form>
          </Box>
        </Grid>
      </Grid>
      <ToastContainer />
    </>
  );
};

export default LoginForm;
