import React from 'react';
import { Navigate } from 'react-router-dom';
import { Box, Typography } from '@mui/material';
import {Icon} from "@iconify/react";

const PrivateRoute = ({ element: Component, allowedRoles }) => {
  const token = localStorage.getItem('token'); 
  const userType = localStorage.getItem('userType'); 
  console.log("token from private route",token);
  console.log("userType from private route",userType);

  const isAuthenticated = !!token;
  const isAuthorized = allowedRoles.includes(userType);

  if (!isAuthenticated) {
    return <Navigate to="/" />;
  }

  return isAuthorized ? (
    <Component />
  ) : (
    <Box
      display="flex"
      justifyContent="center"
      alignItems="center"
      height="100vh"
    >
    <Icon icon="ic:outline-do-not-disturb-alt" width="18" height="18"  style={{color: 'black'}} />
      <Typography variant="h4" fontWeight="bold">
        You are not authorized
      </Typography>
    </Box>
  );
};

export default PrivateRoute;
