import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Box, TextField, Button, Grid } from '@mui/material';
import { fetchBoothNamesAction } from '../redux/actions/action';
import { Icon } from "@iconify/react";
import Autocomplete from '@mui/material/Autocomplete';
import AllDataComponent from './AllDataComponent';

const VolunteerData = () => {
  const dispatch = useDispatch();
  const [searchName, setSearchName] = useState('');
  const [searched, setSearched] = useState(false); 
  const [filteredVoters, setFilteredVoters] = useState([]);
  const [loading, setLoading] = useState(false); 
  const [selectedBoothInAutocomplete, setSelectedBoothInAutocomplete] = useState('');
  const boothNames = useSelector((state) => state.boothNameReducer?.names || []);
  const [booth, setBooth] = useState('');
  const voters = useSelector((state) => state.searchByNameReducer?.data);

  useEffect(() => {
    if (booth.trim() !== '') {
      dispatch(fetchBoothNamesAction(booth));
    }
  }, [booth, dispatch]);

  useEffect(() => {
    if (voters) {
      setFilteredVoters(voters.filter(item => !item.casted));
      const timer = setTimeout(() => {
        setLoading(false);
      }, 1000);
      return () => clearTimeout(timer); 
    }
  }, [voters]);

  const handleSearch = () => {
    setLoading(true); 
    dispatch(searchByNameAction(searchName, selectedBoothInAutocomplete));
    setSearched(true); 
  };

  const handleClear = () => {
    setSearchName('');
    setFilteredVoters([]);
    setSearched(false); 
  };

  const handleInputBoothChange = (event, newValue) => {
    setSelectedBoothInAutocomplete(newValue);
  };

  return (
    <Box sx={{ padding: 3 }}>
      <Grid container sx={{ padding: 3, display: 'flex', justifyContent: 'space-between' }}>
        <Grid item>
          <Box sx={{ borderRadius: '7px', backgroundColor: "white", padding: '2px 5px' }}>
            <Autocomplete
              disablePortal
              size="small"
              options={boothNames}
              sx={{ width: 300 }}
              onChange={handleInputBoothChange}
              renderInput={(params) => (
                <TextField 
                  {...params} 
                  placeholder="Booth name"
                  sx={{
                    "& .MuiOutlinedInput-root": {
                      "&.Mui-focused .MuiOutlinedInput-notchedOutline": {
                        borderColor: "#888888", 
                      },
                      "&:hover .MuiOutlinedInput-notchedOutline": {
                        borderColor: "#888888", 
                      },
                      "& .MuiOutlinedInput-notchedOutline": {
                        borderColor: "#888888", 
                      }
                    }
                  }}
                />
              )}
            />
          </Box> 
        </Grid>
        <Grid item>
          <TextField
            size="small"
            placeholder="Search by entering a name"
            variant="outlined"
            autoComplete="off" 
            value={searchName}
            onChange={(e) => setSearchName(e.target.value)}
            InputProps={{
              endAdornment: <Icon icon="material-symbols-light:search" width="25" height="25" style={{ color: 'black' }} />,
            }}
            sx={{
              "& .MuiOutlinedInput-root": {
                "&.Mui-focused .MuiOutlinedInput-notchedOutline": {
                  borderColor: "#3e4241",
                },
                "&:hover .MuiOutlinedInput-notchedOutline": {
                  borderColor: "#3e4241",
                },
                "& .MuiOutlinedInput-notchedOutline": { borderColor: "#3e4241", }
              },
              mr: 2
            }}
          />
          <Button
            variant="contained"
            onClick={handleSearch}
            sx={{
              backgroundColor: '#EE8832',
              color: 'white',
              fontWeight: 'bold',
              '&:hover': {
                backgroundColor: '#EE8832',
                color: 'white',
              },
            }}
          >
            Search
          </Button>
        </Grid>
      </Grid>

      {selectedBoothInAutocomplete && <AllDataComponent boothName={selectedBoothInAutocomplete} />}
    </Box>
  );
};

export default VolunteerData;
