import React from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import LoginPage from './pages/LoginPage';
import VolunteerPage from './pages/VolunteerPage';
import LeadPage from './pages/LeadPage';
import './App.css';

function App() {
  const token = localStorage.getItem('token');
  const userType = localStorage.getItem('userType');

  return (
    <BrowserRouter>
      <Routes>
        <Route
          path="/"
          element={
            token ? (
              userType === 'volunteer' ? (
                <VolunteerPage />
              ) : userType === 'lead' ? (
                <LeadPage />
              ) : (
                <Navigate to="/" />
              )
            ) : (
              <LoginPage />
            )
          }
        />
        {/* LoginPage should only be rendered if not already logged in */}
        <Route
          path="/login"
          element={token ? <Navigate to="/" /> : <LoginPage />}
        />
        {/* Redirect to login page if not logged in */}
        <Route
          path="/VolunteerData"
          element={token && userType === 'volunteer' ? <VolunteerPage /> : <Navigate to="/login" />}
        />
        {/* Redirect to login page if not logged in */}
        <Route
          path="/LeadData"
          element={token && userType === 'lead' ? <LeadPage /> : <Navigate to="/login" />}
        />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
