import React from 'react';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import { Box } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { Icon } from '@iconify/react';

const ResponsiveAppBar = () => {
  const navigate = useNavigate();
  const username = localStorage.getItem('name');

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userType');
    localStorage.removeItem('name');

    console.log("this is token after logout", localStorage.getItem('token')); // should be null
    console.log("this is username after logout", localStorage.getItem('name')); // should be null

    navigate('/');
  };

  return (
    <AppBar position="static" sx={{ backgroundColor: '#EE8832' }}>
      <Toolbar>
        <Typography variant="h6" sx={{ flexGrow: 1, color: 'white' }}>
          Voter Data
        </Typography>
        <Box sx={{ display: 'flex', alignItems: 'center', mr: 2 }}>
          <Icon icon="lets-icons:user-alt-fill" width="24" height="24" style={{ color: 'white' }} />
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <Typography variant="subtitle1" sx={{ marginRight: 1, color: 'white', fontWeight: 'bold', fontSize: '20px' }}>
              {username}
            </Typography>
          </Box>
        </Box>
        <Button
          color="inherit"
          onClick={handleLogout}
          sx={{ backgroundColor: 'white', color: '#EE8832', textTransform: 'none', letterSpacing: '1.3px', fontSize: '15px', padding: '5px 20px', fontWeight: 'bold', '&:hover': { color: '#EE8832', backgroundColor: '#ffffff' } }}
        >
          Logout
        </Button>
      </Toolbar>
    </AppBar>
  );
};

export default ResponsiveAppBar;
