import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Box, Typography, TextField, Button } from '@mui/material';
import VoterTable from './VoterTable';
import { searchByNameAction } from '../redux/actions/action';
import { Icon } from "@iconify/react";

const VolunteerData = () => {
  const dispatch = useDispatch();
  const [searchName, setSearchName] = useState('');
  const [filteredVoters, setFilteredVoters] = useState([]);
  const voters = useSelector((state) => state.searchByNameReducer?.data);

  // Update filteredVoters whenever voters state changes
  useEffect(() => {
    if (voters) {
      setFilteredVoters(voters.filter(item => !item.casted));
    }
  }, [voters]);

  const handleSearch = () => {
    dispatch(searchByNameAction(searchName));
  };

  const handleClear = () => {
    setSearchName('');
    setFilteredVoters([]);
  };

  return (
    <Box sx={{ padding: 3, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <Box sx={{ display: 'flex', mb: 3, justifyContent: 'end' }}>
        <TextField
          size="small"
          placeholder="Search by entering a name"
          variant="outlined"
          value={searchName}
          onChange={(e) => setSearchName(e.target.value)}
          sx={{
            "& .MuiOutlinedInput-root": {
              "&.Mui-focused .MuiOutlinedInput-notchedOutline": {
                borderColor: "#3e4241",
              },
              "&:hover .MuiOutlinedInput-notchedOutline": {
                borderColor: "#3e4241",
              },
              "& .MuiOutlinedInput-notchedOutline": { borderColor: "#3e4241", }
            }, mr: 2
          }}
        />

        <Button
          variant="contained"
          onClick={handleSearch}
          sx={{
            backgroundColor: '#EE8832',
            color: 'white',
            fontWeight: 'bold',
            '&:hover': {
              backgroundColor: '#EE8832',
              color: 'white',
            },
          }}
        >
          Search
        </Button>
      </Box>
      {filteredVoters.length === 0 ? (
        // Replace 'https://example.com/your-image.jpg' with your image URL
        <img src="https://t4.ftcdn.net/jpg/05/86/21/03/360_F_586210337_WOGOw0l7raEB8F61Muc4hWbvVcyQdk9Z.jpg" alt="No Data" style={{ width: '100%', maxWidth: '400px' }} />
      ) : (
        <VoterTable voters={filteredVoters} handleClear={handleClear} />
      )}
    </Box>
  );
};

export default VolunteerData;
