import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Box, Typography, TextField, Button } from '@mui/material';
import VoterTable from './VoterTable';
import { searchByNameAction } from '../redux/actions/action';

const VolunteerData = () => {
  const dispatch = useDispatch();
  const [searchName, setSearchName] = useState('');
  const [filteredVoters, setFilteredVoters] = useState([]);
  const voters = useSelector((state) => state.searchByNameReducer?.data);

  // Update filteredVoters whenever voters state changes
  useEffect(() => {
    if (voters) {
      setFilteredVoters(voters.filter(item => !item.casted));
    }
  }, [voters]);

  const handleSearch = () => {
    dispatch(searchByNameAction(searchName));
  };

  const handleClear = () => {
    setSearchName('');
    setFilteredVoters([]);
  };

  return (
    <Box sx={{ padding: 3 }}>
      <Box sx={{ display: 'flex', mb: 3,justifyContent:'end' }}>
        <TextField
          label="Name"
          variant="outlined"
          value={searchName}
          onChange={(e) => setSearchName(e.target.value)}
          sx={{ mr: 2 }}
        />
        <Box sx={{ boxShadow: '0px 0px 3px #888888', borderRadius: '7px', backgroundColor: "white", padding: '2px 5px' }}>
    <TextField
      size="small"
      placeholder="Search by entering a name"
      value={searchName}

      InputProps={{
        endAdornment: <Icon icon="material-symbols-light:search" width="25" height="25" style={{ color: 'black' }} />,
      }}
      sx={{
        "& .MuiOutlinedInput-root": {
          "&.Mui-focused .MuiOutlinedInput-notchedOutline": {
            borderColor: "transparent", 
          },
          "&:hover .MuiOutlinedInput-notchedOutline": {
            borderColor: "transparent", 
          },
          "& .MuiOutlinedInput-notchedOutline": {
            borderColor: "transparent", 
          }
        }
      }}
    />
  </Box>
        <Button
          variant="contained"
          onClick={handleSearch}
          sx={{
            backgroundColor: 'black',
            color: 'white',
            fontWeight: 'bold',
            '&:hover': {
              backgroundColor: 'black',
              color: 'white',
            },
          }}
        >
          Search
        </Button>
      </Box>
      {filteredVoters.length === 0 ? (
        <Typography>No Data</Typography>
      ) : (
        <VoterTable voters={filteredVoters} handleClear={handleClear} />
      )}
    </Box>
  );
};

export default VolunteerData;
