import React, { useState } from 'react';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import MenuItem from '@mui/material/MenuItem';
import IconButton from '@mui/material/IconButton';
import { Box, FormControl, InputLabel, Select } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const ResponsiveAppBar = () => {
    const [anchorEl, setAnchorEl] = useState(null);
    const [section, setSection] = useState('all');
    const navigate = useNavigate();
    const username = localStorage.getItem('username');

    const handleMenu = (event) => {
        setAnchorEl(event.currentTarget);
    };

    const handleClose = () => {
        setAnchorEl(null);
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('username');
        navigate('/');
    };

    const handleSectionChange = (event) => {
        console.log('Section changed to:', event.target.value);
        setSection(event.target.value);
    };

    return (
        <AppBar position="static" sx={{ backgroundColor: '#EE8832' }}>
            <Toolbar>
                <Typography variant="h6" component="div" sx={{ flexGrow: 1, color: 'white' }}>
                    Voter Data
                </Typography>

                    <Typography variant="subtitle1" sx={{ marginRight: 5, color: 'white',fontWeight:'bold',fontSize:'20px' }}>
                       {localStorage.getItem('name')}

                    </Typography>

                <Button color="inherit" onClick={handleLogout} variant="outlined" sx={{ borderColor: 'white', color: 'white' }}>
                    Logout
                </Button>
            </Toolbar>
        </AppBar>
    );
};

export default ResponsiveAppBar;
