// App.js

import React from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import LoginPage from './pages/LoginPage';
import VolunteerPage from './pages/VolunteerPage';
import LeadPage from './pages/LeadPage';
import './App.css';

function App() {
  const token = localStorage.getItem('token');
  const userType = localStorage.getItem('userType');

  return (
    <BrowserRouter>
      <Routes>
        <Route
          path="/"
          element={
            token ? (
              userType === 'volunteer' ? (
                <VolunteerPage />
              ) : userType === 'lead' ? (
                <LeadPage />
              ) : (
                <Navigate to="/login" />
              )
            ) : (
              <LoginPage />
            )
          }
        />
        <Route path="/login" element={<LoginPage />} />
        <Route path="/VolunteerData" element={token && userType === 'volunteer' ? <VolunteerPage /> : <Navigate to="/login" />} />
        <Route path="/LeadData" element={token && userType === 'lead' ? <LeadPage /> : <Navigate to="/login" />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
