// Inside your Redux action file
export const loginAction = (formData) => {
  console.log("Login action triggered with formData:", formData);
  return async (dispatch) => {
    dispatch({ type: LOGIN_REQUEST });
    try {
      const data = await loginData(formData);
      console.log("in action --------------->",data);

      localStorage.setItem('userType', data.user.user_type);
      localStorage.setItem('token', data.user.token);
      localStorage.setItem('username', data.user.username);

      axios.defaults.headers.common['Authorization'] = data.user.token;

      dispatch({ type: LOGIN_SUCCESS, payload: data });

      // Navigation logic moved here
      if (data.user.user_type === 'volunteer') {
        console.log("login volunteer");
        toast.success('Login successful!', {
          position: 'top-right',
          autoClose: 5000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          theme: 'colored',
        });
        navigate('/VolunteerData');
      } else {
        console.log("login lead");
        navigate('/LeadData');
      }
    } catch (error) {
      dispatch({ type: LOGIN_FAILURE, payload: error.message });
    }
  };
};
