import React, { useEffect, useState } from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import LoginPage from './pages/LoginPage';
import VolunteerPage from './pages/VolunteerPage';
import LeadPage from './pages/LeadPage';
import './App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userType, setUserType] = useState('');

  // Fetch token and user type from local storage on initial load
  useEffect(() => {
    const token = localStorage.getItem('token');
    const userType = localStorage.getItem('userType');

    // Check if token and user type exist in local storage
    if (token && userType) {
      setIsLoggedIn(true);
      setUserType(userType);
    }
  }, []);

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<LoginPage />} />
        <Route path="/VolunteerData" element={isLoggedIn && userType === 'volunteer' ? <VolunteerPage /> : <Navigate to="/" />} />
        <Route path="/LeadData" element={isLoggedIn && userType === 'lead' ? <LeadPage /> : <Navigate to="/" />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
