import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { searchByNameAction } from '../redux/actions/action';
import {  TextField, Button} from '@mui/material';
import VoterTable from './VoterTable';
import { Box, Typography, FormControl, Select, MenuItem,Grid } from '@mui/material';
import AllDataComponent from './AllDataComponent';
import CastedDataComponent from './CastedDataComponent';
import NotCastedDataComponent from './NotCastedDataComponent';

const LeadData = () => {
  const dispatch = useDispatch();
  const [searchName, setSearchName] = useState('');
  const [searchData, setSearchData] = useState([]); // State to hold search results

  const handleSearch = () => {
    dispatch(searchByNameAction(searchName))
      .then((response) => {
        setSearchData("lead search response",response); // Update search data state with search results
      });
  };

  const [section, setSection] = useState('all');

  const handleSectionChange = (event) => {
    const value = event.target.value;
    setSection(value);
  };

  return (
    <Box sx={{ padding: 3 }}>
      <Typography variant="h4">
        Voters Data
      </Typography>
      <Grid container sx={{ display: 'flex', justifyContent: 'space-between' }}>
        <Grid item>
          <FormControl sx={{ minWidth: 120, marginBottom: 3 }}>
            <Select value={section} onChange={handleSectionChange}>
              <MenuItem value="all">All Voters</MenuItem>
              <MenuItem value="casted">Casted Votes</MenuItem>
              <MenuItem value="not-casted">Not Casted Votes</MenuItem>
            </Select>
          </FormControl>
        </Grid>
        <Grid item>
          <TextField
            size="small"
            label="Name"
            variant="outlined"
            value={searchName}
            onChange={(e) => setSearchName(e.target.value)}
            sx={{ mr: 2 }}
          />
          <Button sx={{ padding: '8px', marginBottom: '25px', textTransform: 'none' }} variant="contained" onClick={handleSearch}>
            Search
          </Button>
        </Grid>
      </Grid>
      {searchName.trim() !== '' ? (
        <VoterTable voters={searchData} />
      ) : (
        <>
          {section === 'all' && <AllDataComponent />}
          {section === 'casted' && <CastedDataComponent />}
          {section === 'not-casted' && <NotCastedDataComponent />}
        </>
      )}
    </Box>
  );
};

export default LeadData;
